import React from 'react';
import { render } from 'react-dom';
import 'react-mdl/extra/material.js';
import { Button, Card } from 'react-mdl';
import 'react-mdl/extra/material.css';
import { Switch, View } from '../../source';
import './app.css';

const ViewOne = ({ onNext, onPrev }) => {
  return (
    <div className="simple-step">
      <h1 className="simple-step__title">Step 1</h1>
      <div className="simple-step-buttons">
        <Button raised ripple onClick={onPrev}>Prev</Button>
        <Button raised ripple accent onClick={onNext}>Next</Button>
      </div>
    </div>
  );
};

const ViewTwo = ({ onNext, onPrev }) => {
  return (
    <div className="simple-step">
      <h1 className="simple-step__title">Step 2</h1>
      <div className="simple-step-buttons">
        <Button raised ripple onClick={onPrev}>Prev</Button>
        <Button raised ripple accent onClick={onNext}>Next</Button>
      </div>
    </div>
  );
};

class Stepper extends React.Component {
  constructor(props) {
    super(props);
    this.onPrev = this.onPrev.bind(this);
    this.onNext = this.onNext.bind(this);
    this.state = {
      active: 0,
      views: ['one', 'two']
    };
  }

  onPrev() {
    this.setState({
      active: this.state.active - 1
    });
  }

  onNext() {
    this.setState({
      active: this.state.active + 1
    });
  }

  render() {
    return (
      <div className="simple">
        <Card style={{ width: '100%' }} shadow={0}>
          <Switch current={this.state.views[this.state.active]}>
            <View viewId="one">
              <ViewOne onNext={this.onNext} onPrev={this.onPrev} />
            </View>
            <View viewId="two">
              <ViewTwo onNext={this.onNext} onPrev={this.onPrev}  />
            </View>
          </Switch>
        </Card>
      </div>
    );
  }
}

render(<Stepper />, document.getElementById('root'));




